import React, { Component } from "react";
import ReactDOM from "react-dom";

import "./index.css";
import logo from "./logo.svg";
import "./App.css";

import { createStore } from 'redux';

const initialState = { count: 0 };

const reducer = (state=initialState,action) => {

  console.log(state)

